import React from "react";
import { Route, Switch, withRouter } from "react-router-dom";
import DamlLedger from "@daml/react";
import Header from "../Header/Header";
import Sidebar from "../Sidebar/Sidebar";
import WholeSaleContract from "../../pages/wholesale/WholesaleContract";
import EFContract from "../../pages/retail/EFContract";
import FundingRequest from "../../pages/fundingrequest/FundingRequest";
import FundingApproval from "../../pages/fundingapproval/FundingApproval";
import { useUserState } from "../../context/UserContext";
import { wsBaseUrl, httpBaseUrl } from "../../config";
import useStyles from "./styles";

const Layout = () => {
  const classes = useStyles();
  const user = useUserState();

  if(!user.isAuthenticated){
    return null;
  } else {
    return (
      <DamlLedger party={user.party} token={user.token} httpBaseUrl={httpBaseUrl} wsBaseUrl={wsBaseUrl}>
        <div className={classes.root}>
            <>
              <Header />
              <Sidebar />
              <div className={classes.content}>
                <div className={classes.fakeToolbar} />
                <Switch>
                  <Route path="/app/wholesale" component={WholeSaleContractList} />
                  <Route path="/app/retail" component={EFContractList} />
                  <Route path="/app/fundingrequest" component={FundingRequestList} />
                  <Route path="/app/fundingapproved" component={FundingApprovalList} />
                </Switch>
              </div>
            </>
        </div>
      </DamlLedger>
    );
  }
}

export default withRouter(Layout);
